var assert = require('assert'),
NodeNeuralNetwork = require('../src/NodeNeuralNetwork');

var Perceptron = NodeNeuralNetwork.Architect.Perceptron,
LSTM = NodeNeuralNetwork.Architect.LSTM,
Layer = NodeNeuralNetwork.Layer,
Network = NodeNeuralNetwork.Network,
Trainer = NodeNeuralNetwork.Trainer;

describe('LayerClass', function() {
	describe('Constructeur', function() {
		it("initialise size with 1 with 1 in parametre",function(){
			var layer = new Layer(1);
			assert.equal(layer.size,1);
			assert.equal(layer.listNeurons.length,1);
			assert.equal(layer.neurons().length,1);


		})

		it("initialise size with 2 with 2 in parametre",function(){
			var layer = new Layer(2);
			assert.equal(layer.size,2);
			assert.equal(layer.listNeurons.length,2);
			assert.equal(layer.neurons().length,2);

		})

		it("initialise size with 0 with nothing in parametre",function(){
			var layer = new Layer();
			assert.equal(layer.size,0);
			assert.equal(layer.listNeurons.length,0);
			assert.equal(layer.neurons().length,0);
		})
	});

	describe('Activate', function() {
		
	});

})